mapper.select:
SELECT FROM tb_track
CUS_ID=#{advancedQueryCondition.cusId}
ORDER BY CREATE_TIME DESC

SELECT DATE_FORMAT(tt.CREATE_TIME, '%Y-%m-%d') AS DATE,COUNT(*) AS TRACK_COUNT,net_track.NET_TRACK_COUNT,tel_track.TEL_TRACK_COUNT, net_teach_track.NET_TEACH_TRACK_COUNT ,tt.USER_ID, ta.username AS USERNAME FROM tb_track tt JOIN tb_admin ta ON tt.USER_ID=ta.id LEFT JOIN (SELECT DATE_FORMAT(tt.CREATE_TIME, '%Y-%m-%d') AS DATE,COUNT(*) AS TEL_TRACK_COUNT, tt.USER_ID AS USER_ID FROM tb_track tt JOIN tb_admin ta ON tt.USER_ID=ta.id WHERE tt.TRACK_TYPE=1 GROUP BY DATE_FORMAT(tt.CREATE_TIME, '%Y-%m-%d'),tt.USER_ID) tel_track ON tel_track.USER_ID = tt.USER_ID AND DATE_FORMAT(tt.CREATE_TIME, '%Y-%m-%d')=tel_track.DATE LEFT JOIN (SELECT DATE_FORMAT(tt.CREATE_TIME, '%Y-%m-%d') AS DATE,COUNT(*) AS NET_TRACK_COUNT, tt.USER_ID AS USER_ID FROM tb_track tt JOIN tb_admin ta ON tt.USER_ID=ta.id WHERE tt.TRACK_TYPE=2 GROUP BY DATE_FORMAT(tt.CREATE_TIME, '%Y-%m-%d'),tt.USER_ID) net_track ON net_track.USER_ID = tt.USER_ID AND DATE_FORMAT(tt.CREATE_TIME, '%Y-%m-%d')=net_track.DATE LEFT JOIN (SELECT DATE_FORMAT(tt.CREATE_TIME, '%Y-%m-%d') AS DATE,COUNT(*) AS NET_TEACH_TRACK_COUNT, tt.USER_ID AS USER_ID FROM tb_track tt JOIN tb_admin ta ON tt.USER_ID=ta.id WHERE tt.TRACK_TYPE=3 GROUP BY DATE_FORMAT(tt.CREATE_TIME, '%Y-%m-%d'),tt.USER_ID) net_teach_track ON net_teach_track.USER_ID = tt.USER_ID AND DATE_FORMAT(tt.CREATE_TIME, '%Y-%m-%d')=net_teach_track.DATE where
ta.deptid IN ${dept.depId}
tt.USER_ID=#{advancedQueryCondition.userId}
AND ta.username LIKE CONCAT('%',#{searchCondition.value},'%')
AND tt.CREATE_TIME >= #{advancedQueryCondition.rangeDate.startDate}
AND tt.CREATE_TIME < #{advancedQueryCondition.rangeDate.endDate}
GROUP BY DATE_FORMAT(tt.CREATE_TIME, '%Y-%m-%d'),tt.USER_ID ORDER BY DATE DESC
